import React from "react";
import ScrollToBottom from "react-scroll-to-bottom";
import "./Messages.css";

const Messages = ({ messagesArray, name }) => (
  <ScrollToBottom>
    {messagesArray.map((message, i) => {
      <div key={i}>
        <Message message={message} name={name} />
      </div>;
    })}
  </ScrollToBottom>
);

export default Messages;
